<script>
// List of stations injected from CSV
const stations = {{ js_array }};

// Get query parameter from URL
function getQueryParam(param) {
  const urlParams = new URLSearchParams(window.location.search);
  return urlParams.get(param);
}

// Populate dropdown
const dropdown = document.getElementById("station-dropdown");
const selectedStation = getQueryParam("station") || stations[0];

stations.forEach(station => {
  const option = document.createElement("option");
  option.value = station;
  option.text = station;
  if(station === selectedStation) option.selected = true;
  dropdown.add(option);
});

// Handle change
function onStationChange() {
  const station = dropdown.value;
  window.location.href = `station.html?station=${station}`;
}
</script>
